import {mapParams} from "../maps/Level1";

class Enemy {
    constructor(
        public context:CanvasRenderingContext2D,
        public randomOffset = {
            x: Math.floor(Math.random() * 8),
            y: Math.floor(Math.random() * 8),
        },
        public currentPosition = {
            x: 0,
            y: 0,
        },
        public enemyParams = {
            width: 10,
            height: 10
        },
        ) {
    }

    public drawEnemy(context:CanvasRenderingContext2D) {
        // set initial coords of enemy
        this.currentPosition.x = mapParams.startX + this.randomOffset.x
        this.currentPosition.y = mapParams.startY + this.randomOffset.y

        // draw a 2d representation
        context.beginPath()
        context.rect( this.currentPosition.x , this.currentPosition.y, this.enemyParams.width, this.enemyParams.height )
        context.strokeStyle = 'red'
        context.stroke()
        context.closePath()
    }

    public move() {
        requestAnimationFrame(() => this.move())
        context.clearRect(this.currentPosition.x + tick , this.currentPosition.y, this.enemyParams.width, this.enemyParams.height)

        context.beginPath()
        context.rect( this.currentPosition.x + tick , this.currentPosition.y, this.enemyParams.width, this.enemyParams.height )
        context.strokeStyle = 'red'
        context.stroke()
        context.closePath()
    }
}

export default Enemy;
