import {mapParams} from "../maps/Level1";

class Enemy {
    constructor(
        public context: CanvasRenderingContext2D,
        public isMoving: boolean = false,
        public animationFrameId: number | null = null,
        public randomOffset = {
            x: Math.floor(Math.random() * 8),
            y: Math.floor(Math.random() * 8),
        },
        public currentPosition = {
            x: 0,
            y: 0,
        },
        public enemyParams = {
            width: 10,
            height: 10
        },
    ) {

    }

    public drawEnemy() {
        // set initial coords of enemy
        this.currentPosition.x = mapParams.startX + this.randomOffset.x
        this.currentPosition.y = mapParams.startY + this.randomOffset.y

        // draw a 2d representation
        this.context.beginPath()
        this.context.rect(this.currentPosition.x, this.currentPosition.y, this.enemyParams.width, this.enemyParams.height)
        this.context.strokeStyle = 'red'
        this.context.stroke()
        this.context.closePath()
    }

    public moveRight() {
        // increment x, y is constant
        this.currentPosition.x += 1

        // clear prev render
        this.context.fillRect(this.currentPosition.x - 1, this.currentPosition.y - 1 , this.enemyParams.width + 1, this.enemyParams.height + 2)

        // and place a new figure on canvas
        this.context.beginPath()
        this.context.rect(this.currentPosition.x, this.currentPosition.y, this.enemyParams.width, this.enemyParams.height)
        this.context.strokeStyle = 'red'
        this.context.stroke()
        this.context.closePath()
    }

    public moveDown() {
        // increment x, y is constant
        this.currentPosition.y += 1

        // clear prev render
        this.context.fillRect(this.currentPosition.x, this.currentPosition.y + 1 , this.enemyParams.width + 1, this.enemyParams.height)

        // and place a new figure on canvas
        this.context.beginPath()
        this.context.rect(this.currentPosition.x, this.currentPosition.y, this.enemyParams.width, this.enemyParams.height)
        this.context.strokeStyle = 'red'
        this.context.stroke()
        this.context.closePath()
    }

    public move() {
        this.animationFrameId = requestAnimationFrame(() => this.move())
        if (!this.isMoving) {

        }

        if (this.currentPosition.x <= mapParams.rightBorder) {
            this.moveRight()
        }

        // debug
        console.log(`this.currentPosition.x`)
        console.log(this.currentPosition.x)
        console.log(`this.currentPosition.x > mapParams.rightBorder`)
        console.log(this.currentPosition.x > mapParams.rightBorder)
        //

        /*
        if(this.currentPosition.x <= mapParams.rightBorder) {
            this.moveRight()
        } else if (this.currentPosition.x == mapParams.rightBorder ) {
            this.moveDown()
        }
         */


        setTimeout(() => {
            this.stop()
        }, 15000)
    }

    public stop() {
        if (this.animationFrameId !== null) {
            cancelAnimationFrame(this.animationFrameId!)
        }
    }
}

export default Enemy;
