import React, {useEffect, useRef, useState} from 'react';
import drawMap, {mapParams} from '../maps/Level1'
import Enemy from "../enemies/Enemy";


const Game = () => {
    const canvas = useRef<HTMLCanvasElement>(null)
    const enemy = new Enemy()
    const [isGameStarted, setIsGameStarted] = useState<boolean>(false)

    useEffect(() => {
        const context = canvas.current?.getContext('2d')
        if (context !== null) {
            // draw level map
            drawMap(context!)
            // draw enemies
            enemy.drawEnemy(context!)
            // start main game loop
            while(isGameStarted){
                const tick = new Date().getMilliseconds()
            }
        }

    }, [isGameStarted])

    return (
        <div>
            <canvas ref={canvas!} id="canvas" width={mapParams.width} height={mapParams.height} style={{border: "2px solid green"}}></canvas>
        </div>
    )
}

export default Game;
