import React, {useEffect, useRef} from 'react';
import drawMap, {mapParams} from '../maps/Level1'


const Game = () => {
    const canvas = useRef<HTMLCanvasElement>(null)
    useEffect(() => {
        const context = canvas.current?.getContext('2d')
        drawMap(context)
    }, [])

    return (
        <div>
            <canvas ref={canvas!} id="canvas" width={mapParams.width} height={mapParams.height}></canvas>
        </div>
    )
}

export default Game;
