export const mapParams = {
    width: 600,
    height: 300,
    gridStep: 20,
}

const drawMap = (context:CanvasRenderingContext2D) => {
    // first line
    context.rect(
        0,
        20,
        mapParams.width /2,
        mapParams.gridStep)
    context.fill()
    // turn to the right
    context.rect(
        mapParams.width /2,
        100,
        mapParams.width /2,
        mapParams.gridStep)
    context.fillStyle = 'green'
    context.fill()
}

export default drawMap;
