import React, {useEffect, useRef, useState} from 'react';
import drawMap, {mapParams} from '../maps/Level1'
import Enemy from "../enemies/Enemy";

const gameParams = {
    tickTimeout: 100,
}

const Game = () => {
    const canvas = useRef<HTMLCanvasElement>(null)
    const enemy = new Enemy()
    const [isGameStarted, setIsGameStarted] = useState<boolean>(false)
    const [mainLoopInterval, setMainLoopInterval] = useState<NodeJS.Timer>()
    const [tick, setTick] = useState(0)

    useEffect(() => {
        const context = canvas.current?.getContext('2d')
        if (context !== null) {
            // draw level map
            drawMap(context!)
            // draw enemies
            enemy.drawEnemy(context!)
            // start main game loop
            if (isGameStarted) {
                setMainLoopInterval(setInterval(() => {
                    // increment main loop tick
                    setTick(tick + 1)
                    enemy.move(context, tick)
                }, gameParams.tickTimeout))
            } else {
                clearInterval(mainLoopInterval)
            }
        }

    }, [isGameStarted])

    return (
        <section>
            <div>
                <canvas ref={canvas!} id="canvas" width={mapParams.width} height={mapParams.height}
                        style={{border: "2px solid green"}}></canvas>
            </div>
            <div>
                <button onClick={() => setIsGameStarted(true)}>Start teh game</button>
                <button onClick={() => setIsGameStarted(false)}>End teh game</button>
            </div>
        </section>
    )
}

export default Game;
