import {mapParams} from "../maps/Level1";

class Enemy {
    constructor(
        public randomOffset = {
            x: Math.floor(Math.random() * 10),
            y: Math.floor(Math.random() * 10),
        }
        ) {
    }

    public static drawEnemy(context:CanvasRenderingContext2D) {
        context.beginPath()
        context.rect(mapParams.startX + 2 , mapParams.startY + 2, mapParams.gridStep /2, mapParams.gridStep / 2 )
        context.strokeStyle = 'red'
        context.stroke()
        context.closePath()
    }
}

export default Enemy;
