import {mapParams} from "../maps/Level1";

class Enemy {
    constructor(
        public randomOffset = {
            x: Math.floor(Math.random() * 8),
            y: Math.floor(Math.random() * 8),
        },
        public currentPosition = {
            x: 0,
            y: 0,
        }
        ) {
    }

    public drawEnemy(context:CanvasRenderingContext2D) {
        this.currentPosition.x = mapParams.startX + this.randomOffset.x
        this.currentPosition.y = mapParams.startY + this.randomOffset.y
        context.rect( this.currentPosition.x , this.currentPosition.y, mapParams.gridStep /2, mapParams.gridStep / 2 )
        context.strokeStyle = 'red'
        context.stroke()
        context.closePath()
    }

    public move(context:CanvasRenderingContext2D, tick:number) {
        context.clearRect(mapParams.startX + this.randomOffset.x , mapParams.startY + this.randomOffset.y, mapParams.gridStep /2, mapParams.gridStep / 2)
        context.beginPath()
        context.rect(mapParams.startX + this.randomOffset.x + tick , mapParams.startY + this.randomOffset.y, mapParams.gridStep /2, mapParams.gridStep / 2 )
        context.strokeStyle = 'red'
        context.stroke()
        context.closePath()
    }
}

export default Enemy;
