        public randomOffset = {
            x: Math.floor(Math.random() * 8),
            y: Math.floor(Math.random() * 8),
        }
        ) {
    }

    public drawEnemy(context:CanvasRenderingContext2D) {
        context.beginPath()
        context.rect(mapParams.startX + this.randomOffset.x , mapParams.startY + this.randomOffset.y, mapParams.gridStep /2, mapParams.gridStep / 2 )
        context.strokeStyle = 'red'
        context.stroke()
        context.closePath()
    }

    public move(context:CanvasRenderingContext2D) {

    }
}

export default Enemy;
